// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../interface/IVipNFT.sol";
import "../base/BaseMiningPool.sol";
import "../interface/INFTInvitation.sol";


contract NFTToMiningPool is BaseMiningPool
{
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    event RewardAdded(uint256 reward);
    event StakedTokenEvent(address indexed user, uint256 amount);
    event WithdrawTokenEvent(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);

    INFTInvitation nftInvitation;
    mapping(address => uint256) private _tokenBalanceOf;    // 质押的token的量
    IVipNFT public vipNFT;
    address public burnAddr;

    constructor(
        address vipNFT_,
        address nftInvitation_,
        address rewardtoken_,
        address stakeToken_,
        uint256 rewardTotal_,
        uint256 duration_,
        uint256 starttime_
    ) {
        _initPool(
            rewardtoken_,
            stakeToken_,
            rewardTotal_,
            duration_,
            starttime_
        );
        vipNFT = IVipNFT(vipNFT_);
        nftInvitation = INFTInvitation(nftInvitation_);
    }

    function myStake() public view returns (uint256, uint256) {
        return (_tokenBalanceOf[msg.sender], balanceOf(msg.sender));
    }

    // sender 当前已经可以获得的奖励
    function earned(address account)
        public view virtual override
        returns (uint256)
    {
        uint256 _rwdV = _earned(account);
        return _rwdV - _rwdV * nftInvitation.directRewardRate() / 100;
    }

    // LP
    // stake visibility is public as overriding LPTokenWrapper's stake() function
    function stake(uint256 amount, uint256 _nftNo)
        public
        updateReward(msg.sender)
        checkStart
    {
        // 质押NFT。
        require(amount > 0, "MiningPool: Cannot stake 0");
        // NFT是不是你的
        if (_nftNo > 0){
            require(vipNFT.ownerOf(_nftNo) == msg.sender, "MiningPool: nft owner error");
        }

        // LP到pool
        stakeToken.safeTransferFrom(msg.sender, address(this), amount);
        // 记录加成LP
        uint256 extraRate = vipNFT.getNFTExtraAddRate(_nftNo);
        _tokenStakeSimple(msg.sender, amount + amount * extraRate / 100);

        _tokenBalanceOf[msg.sender] = amount;      // 记录真实转账数额
        emit StakedTokenEvent(msg.sender, amount);
    }

    // 提币
    function withdrawAll()
        public
        updateReward(msg.sender)
        checkStart
    {
        require(_tokenBalanceOf[msg.sender] > 0, "MiningPool: balance 0");

        uint256 amount = _tokenBalanceOf[msg.sender];
        stakeToken.safeTransfer(msg.sender, amount);
        _tokenWithdrawSimple(msg.sender, balanceOf(msg.sender));

        _tokenBalanceOf[msg.sender] = 0;    // 清零

        emit WithdrawTokenEvent(msg.sender, amount);
    }

    // 更新加成数量
    function updateStakeToken(address _owner, uint256 _extraAddR)
        public onlyAdmin updateReward(_owner)
    {
        if (_tokenBalanceOf[_owner] > 0) {
            uint256 oldBalance = balanceOf(_owner);
            uint256 newBalance = _tokenBalanceOf[_owner] + _tokenBalanceOf[_owner] * _extraAddR / 100;
            if (oldBalance <= newBalance){
                _tokenStakeSimple(_owner, newBalance - oldBalance);
            } else {
                _tokenWithdrawSimple(_owner, oldBalance - newBalance);
            }
        }
    }

    // 领奖
    function drawReward() public updateReward(msg.sender) checkStart {
        // sender 当前已经可以获得的奖励
        uint256 reward = _earned(msg.sender);
        // 分配直推奖励
        uint256 _nftNo = vipNFT.getOwnerNFTNo(msg.sender);
        (uint256 _otherRwd, uint256 _burnRwd) = nftInvitation.allocDirectReward(_nftNo, reward);
        // sender 的收益置空
        rewards[msg.sender] = 0;
        // 向sender转账
        rewardToken.safeTransfer(msg.sender, _otherRwd);
        // burn
        if (_burnRwd > 0) {
            rewardToken.safeTransfer(burnAddr, _burnRwd);
        }
        emit RewardPaid(msg.sender, reward);
    }

    function setVipNFT(IVipNFT _vipNFT)
        public onlyAdmin
    {
        vipNFT = _vipNFT;
    }

    function setNFTInvitation(INFTInvitation _nftInvitation)
        public onlyAdmin
    {
        nftInvitation = _nftInvitation;
    }


    function setBurnAddr(address _addr)
        public onlyAdmin
    {
        burnAddr = _addr;
    }

}
