//SPDX-License-Identifier: MIT
pragma solidity ^0.8.3;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./owner/AdminRole.sol";
import {IVipNFT as INFTCard} from "./interface/IVipNFT.sol";

contract NFTInvitation is AdminRole {
    mapping(uint256 => inviteItem) public inviteDict;   // 所有NFT的邀请数据
    uint256 public directRewardRate;        // 直推奖励比例
    mapping(uint32 => LvConf) public nftLvConf;   // 等级配置数据
    INFTCard public nftCard;

    struct inviteItem {
        uint256 bindNFTId;    // 绑定NFTID。
        mapping(uint32 => uint256) invnftLevelNum;   // 邀请的NFT, 各等级的数量
        uint256 nftExtendRwd;       // 推广奖励
        uint256 rwdedExtend;
    }

    struct LvConf {
        uint8 exists;       // 1存在、0不存在
        uint256 hToken;      // 持有
        uint256 rewdBuyR;       // 购卡奖励比例
    }

    constructor(INFTCard _nftCard, uint256 _directRewardRate){
        nftCard = _nftCard;
        directRewardRate = _directRewardRate;
    }

    function getBindNFTId(uint256 _nftNo)
        public view
        returns (uint256)
    {
        return inviteDict[_nftNo].bindNFTId;
    }

    function getExtendReward(uint256 _nftNo)
        public view
        returns (uint256)
    {
        return inviteDict[_nftNo].nftExtendRwd - inviteDict[_nftNo].nftExtendRwd * directRewardRate / 100;
    }

    function getInvnftLevelNum(uint256 _nftNo, uint32[] memory _levelList)
        public view
        returns (uint256[] memory numList)
    {
        numList = new uint256[](_levelList.length);
        for (uint256 i=0;i < _levelList.length; i++){
            numList[i] = inviteDict[_nftNo].invnftLevelNum[_levelList[i]];
        }
    }

    function isOkToLvup(uint256 _invnftNo, uint32[] memory condLvs, uint256[] memory _condNums)
        public view
        returns(bool isOk)
    {
        isOk = true;
        for (uint256 index = 0; index < condLvs.length; index ++) {
            if (inviteDict[_invnftNo].invnftLevelNum[condLvs[index]] < _condNums[index]){
                isOk = false;
                break;
            }
        }
    }

    // 绑定邀请NFT
    function bindNFT(uint256 _bindNo, uint256 _nftNo, uint32 _nftLv)
        public onlyAdmin
        returns(uint256 burnAmount)
    {
        if (_bindNo > 0) {
            // 有绑过，不行
            require (inviteDict[_nftNo].bindNFTId == 0, "NFTInvitation: nft band");
            // bind是否存在
            require (nftCard.isExists(_bindNo) > 0, "NFTInvitation: bindNft is not exists");
            // 检查
            require (inviteDict[_bindNo].bindNFTId != _nftNo, "NFTInvitation: bind error");
            // bind
            inviteDict[_nftNo].bindNFTId = _bindNo;
            // 添加上线的invnftLevelNum
            for (uint32 i = 0;i <= _nftLv;i++)
            {
                inviteDict[_bindNo].invnftLevelNum[i] += 1;
            }
            // 增加购卡奖励
            uint32 bindNFTLv = nftCard.getNFTLevel(_bindNo);
            uint256 rewdV = addInvBuyReward(bindNFTLv, _bindNo, nftLvConf[_nftLv].hToken);
            // burn掉剩下的钱
            burnAmount = nftLvConf[_nftLv].hToken - rewdV;
        } else {
            burnAmount = 0;
        }
    }

    // 解绑
    function unbindNFT(uint256 _nftNo)
        public onlyAdmin
    {
        require (nftCard.isExists(_nftNo) > 0, "NFTStore: nftNo is not exists");
        inviteDict[_nftNo].bindNFTId = 0;
    }


    // 邀请的NFT要升级
    function invnftLevelup(uint256 _invnftNo, uint32 _newLevel, uint32[] memory condLvs, uint256[] memory _condNums)
        public onlyAdmin
        returns(uint256 burnAmount)
    {
        require (_newLevel > 0, "NFTInvitation: newlevel is 0");
        require (nftLvConf[_newLevel].exists > 0, "NFTInvitation: level is not exists");
        // 检验升级资格
        for (uint256 index = 0; index < condLvs.length; index ++) {
            require (inviteDict[_invnftNo].invnftLevelNum[condLvs[index]] >= _condNums[index],
                "NFTInvitation: condition error");
        }
        if (inviteDict[_invnftNo].bindNFTId > 0) {
            uint256 _bindNo = inviteDict[_invnftNo].bindNFTId;
            uint32 bindNFTLv = nftCard.getNFTLevel(_bindNo);
            // 升级之后的数据变化, 各等级的数量发生变化
            inviteDict[_bindNo].invnftLevelNum[_newLevel] += 1;
            // 增加升级的购卡奖励
            uint256 lvHToken = nftLvConf[_newLevel].hToken - nftLvConf[_newLevel - 1].hToken;
            uint256 rewdV = addInvBuyReward(bindNFTLv, _bindNo, lvHToken);
            // burn掉剩下的钱
            burnAmount = lvHToken - rewdV;
        } else {
            burnAmount = 0;
        }
    }

    // 分配直推奖励,
    function allocDirectReward(uint256 _invnftNo, uint256 _totalRwd)
        public onlyAdmin
        returns (uint256 _otherRwd, uint256 _burnRwd)
    {
        uint256 _extendRwd = _totalRwd * directRewardRate / 100;
        _otherRwd = _totalRwd - _extendRwd;
        uint256 _bindNFTId = inviteDict[_invnftNo].bindNFTId;
        // 有绑定，加给绑定
        if (_bindNFTId > 0){
            // 上线是否大于自己
            if (nftCard.getNFTLevel(_invnftNo) <= nftCard.getNFTLevel(_bindNFTId)) {
                inviteDict[_bindNFTId].nftExtendRwd += _extendRwd;
                _burnRwd = 0;
            } else {
                _burnRwd = _extendRwd;
            }
        } else {
            _burnRwd = _extendRwd;
        }
    }

    function addInvBuyReward(uint32 _bindNFTLv, uint256 _bindNo, uint256 _price)
        private
        returns (uint256 rewdV)
    {
        // 购卡奖励在原有基础上*1.25倍 . v * 1.25 /100 = v / 80
        rewdV = _price * nftLvConf[_bindNFTLv].rewdBuyR / 80;
        inviteDict[_bindNo].nftExtendRwd += rewdV;
    }

    // 领个人的推广奖励
    function drawExtensionReward(uint256 _nftNo)
        public onlyAdmin
        returns (uint256 _rewardV, uint256 _burnRwd)
    {
        // 购卡奖励与直推合并。购卡奖励不需要判断等级，直推和挖矿需要判断等级
        // 找到推广奖励
        uint256 rewdVTotal = inviteDict[_nftNo].nftExtendRwd;
        (_rewardV, _burnRwd) = allocDirectReward(_nftNo, rewdVTotal);
        // 修改数据
        inviteDict[_nftNo].nftExtendRwd = 0;
        inviteDict[_nftNo].rwdedExtend += _rewardV;
    }

    // 配置LvConf
    function addNFTLvConf(
        uint32[] memory _levelList,
        uint256[] memory _hTokenList,
        uint256[] memory _rewdBuyRList
    ) public onlyAdmin {
        for (uint256 index = 0; index < _levelList.length; index++) {
            nftLvConf[_levelList[index]] =
                LvConf(
                    1,
                    _hTokenList[index],
                    _rewdBuyRList[index]
                );
        }
    }

    function removeNFTLvConf(uint32 _level)
        public onlyAdmin
    {
        delete nftLvConf[_level];
    }

    function setNFTCard(INFTCard _nftCard)
        public onlyAdmin
    {
        nftCard = _nftCard;
    }

    function setDirectRewardRate(uint256 _rate)
        public onlyAdmin
    {
        directRewardRate = _rate;
    }

}
