//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import '@openzeppelin/contracts/utils/math/SafeMath.sol';
import "./owner/AdminRole.sol";
import "./interface/INFTInvitation.sol";
import "./interface/IVipNFT.sol";


contract NFTBonus is AdminRole {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    IVipNFT public vipNFT;
    INFTInvitation public nFTInvitation;
    address public burnAddr;
    address public storeAddr;
    mapping(uint32 => uint256) public levelBonusRate; // 按等级的分红比例。
    mapping(uint32 => uint256) public levelBonusReward; // 等级每人的分红数量

    constructor(
        address _vipNFT,
        address _nFTInvitation,
        address _burnAddr
    ) {
        vipNFT = IVipNFT(_vipNFT);
        nFTInvitation = INFTInvitation(_nFTInvitation);
        burnAddr = _burnAddr;
    }

    mapping(address => mapping(uint256 => uint256)) private investors;  //用户奖励  investors[token][number]


    function earned(address token) public view returns (uint256) {
        uint256 nftNo = vipNFT.getOwnerNFTNo(msg.sender);
        return investors[token][nftNo] * 80 / 100;
    }

    function getReward(address token) public {
        uint256 nftNo = vipNFT.getOwnerNFTNo(msg.sender);
        uint256 reward = investors[token][nftNo];
        if (reward > 0) {
            investors[token][nftNo] = 0;

            (uint256 rewardV, uint256 _burnRwd) = nFTInvitation.allocDirectReward(nftNo, reward);
            IERC20(token).safeTransfer(msg.sender, rewardV);

            if (_burnRwd > 0) {
                IERC20(token).safeTransfer(burnAddr, _burnRwd);
            } else {
                IERC20(token).safeTransfer(storeAddr, reward - rewardV);
            }
            emit RewardPaid(msg.sender, reward);
        }
    }

    function release(address token, uint256 amount, uint32 maxLevel) //每个周期执行一次
        external
        onlyAdmin
    {
        require(amount > 0, 'NFTStore: Cannot allocate 0');

        // 计算每个等级的每人的奖励是多少
        uint256 maxNFTNumber = 10000;
        for (uint32 index = 0; index < maxLevel; index++) {
            uint256 lvNum = vipNFT.getNFTsLevelNum(index);
            if (lvNum > 0) {
                levelBonusReward[index] = ((amount * levelBonusRate[index]) / 100) / lvNum;
            } else {
                levelBonusReward[index] = 0;
            }
            maxNFTNumber += lvNum;
        }
        // 为每一个人
        for (uint256 i=10001;i < maxNFTNumber + 1; i++) {
            uint32 _level = vipNFT.getNFTLevel(i);
            if (levelBonusReward[_level] > 0) {
                investors[token][i] = investors[token][i].add(levelBonusReward[_level]);
            }
        }

        emit RewardAdded(amount);
    }

    function getInvestorReward(address token, uint256 _nftNo)
        public onlyAdmin view
        returns (uint256)
    {
        return investors[token][_nftNo];
    }


    // 设置分红比例
    function setLevelBonusRate(uint256[] memory _bonusRate) public onlyAdmin {
        for (uint32 index = 0; index < _bonusRate.length; index++) {
            levelBonusRate[index] = _bonusRate[index];
        }
    }

    function setVipNFT(address _vipNFT) public onlyAdmin {
        vipNFT = IVipNFT(_vipNFT);
    }

    function setBurnAddr(address _burnAddr) public onlyAdmin {
        burnAddr = _burnAddr;
    }

    function setNFTInvitation(address _NFInvitation) public onlyAdmin {
        nFTInvitation = INFTInvitation(_NFInvitation);
    }

    event RewardPaid(address indexed user, uint256 reward);
    event RewardAdded(uint256 reward);

}
